"use client";

import { motion, AnimatePresence } from "motion/react";
import { useState } from "react";
import { ProductGrid } from "./product-grid";
import { CartDrawer } from "./cart-drawer";
import { ProductModal } from "./product-modal";
import { TopBar } from "./top-bar";
import { type Product, type CartItem, products } from "./data";

export default function MinimalShop() {
    const [cart, setCart] = useState<CartItem[]>([]);
    const [selectedProduct, setSelectedProduct] = useState<Product | null>(
        null
    );
    const [isCartOpen, setIsCartOpen] = useState(false);
    const [searchQuery, setSearchQuery] = useState("");

    const addToCart = (product: Product, quantity: number = 1) => {
        setCart((prev) => {
            const exists = prev.find((item) => item.id === product.id);
            if (exists) {
                return prev.map((item) =>
                    item.id === product.id
                        ? { ...item, quantity: item.quantity + quantity }
                        : item
                );
            }
            return [...prev, { ...product, quantity }];
        });
    };

    const removeFromCart = (productId: string) => {
        setCart((prev) => prev.filter((item) => item.id !== productId));
    };

    const filteredProducts = products.filter((product) =>
        product.name.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="h-screen bg-zinc-50 dark:bg-zinc-950">
            <TopBar
                cartItemCount={cart.length}
                onCartClick={() => setIsCartOpen(true)}
                onSearch={setSearchQuery}
            />

            <div className="mx-auto px-2 pt-12 pb-16">
                <ProductGrid
                    products={filteredProducts}
                    onProductSelect={setSelectedProduct}
                />
            </div>

            <AnimatePresence>
                {selectedProduct && (
                    <ProductModal
                        product={selectedProduct}
                        onClose={() => setSelectedProduct(null)}
                        onAddToCart={(product) => {
                            addToCart(product);
                            setSelectedProduct(null);
                            setIsCartOpen(true);
                        }}
                    />
                )}
            </AnimatePresence>

            <AnimatePresence>
                {isCartOpen && (
                    <CartDrawer
                        cart={cart}
                        onClose={() => setIsCartOpen(false)}
                        onRemoveFromCart={removeFromCart}
                    />
                )}
            </AnimatePresence>
        </div>
    );
}

"use client";

import { Search, ShoppingBag, X } from "lucide-react";
import { useState, useEffect, useRef } from "react";
import { motion } from "motion/react";
import Link from "next/link";

interface TopBarProps {
    cartItemCount: number;
    onCartClick: () => void;
    onSearch: (query: string) => void;
}

const categories = [
    "All",
    "Lighting",
    "Kitchenware",
    "Home Decor",
    "Plants",
    "Office",
    "Textiles",
];

export function TopBar({ cartItemCount, onCartClick, onSearch }: TopBarProps) {
    const [isSearchOpen, setIsSearchOpen] = useState(false);
    const [selectedCategory, setSelectedCategory] = useState("All");
    const [isScrolled, setIsScrolled] = useState(false);
    const searchInputRef = useRef<HTMLInputElement>(null);

    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 10);
        };
        window.addEventListener("scroll", handleScroll);
        return () => window.removeEventListener("scroll", handleScroll);
    }, []);

    const handleKeyPress = (e: React.KeyboardEvent) => {
        if (e.key === "Escape") {
            setIsSearchOpen(false);
            searchInputRef.current?.blur();
        }
    };

    return (
        <div
            className={`sticky top-0 z-40 transition-all duration-200 ${
                isScrolled
                    ? "bg-white/90 dark:bg-zinc-900/90 shadow-sm"
                    : "bg-white/80 dark:bg-zinc-900/80"
            } backdrop-blur-sm border-b border-zinc-200 dark:border-zinc-800`}
        >
            <div className="flex items-center justify-between px-3 h-12">
                <Link
                    href="https://kokonutui.com/"
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-sm font-medium shrink-0"
                >
                    Shop
                </Link>

                <div className="flex-1 px-8 overflow-x-auto flex items-center justify-center gap-6 scrollbar-none">
                    {categories.map((category) => (
                        <button
                            type="button"
                            key={category}
                            className={`whitespace-nowrap transition-colors ${
                                selectedCategory === category
                                    ? "text-zinc-900 dark:text-white text-sm font-medium"
                                    : "text-zinc-500 dark:text-zinc-400 text-sm hover:text-zinc-900 dark:hover:text-white"
                            }`}
                            onClick={() => setSelectedCategory(category)}
                        >
                            {category}
                        </button>
                    ))}
                </div>

                <div className="flex items-center gap-1.5 shrink-0">
                    <motion.div
                        className="relative"
                        initial={false}
                        animate={{ width: isSearchOpen ? "auto" : 0 }}
                    >
                        <input
                            ref={searchInputRef}
                            type="text"
                            placeholder="Search products..."
                            className={`w-48 sm:w-56 bg-zinc-100 dark:bg-zinc-800 rounded-md text-sm px-3 py-1.5 
                                focus:outline-none focus:ring-1 focus:ring-zinc-300 dark:focus:ring-zinc-700
                                transition-all duration-200 ${
                                    isSearchOpen ? "opacity-100" : "opacity-0"
                                }`}
                            onChange={(e) => onSearch(e.target.value)}
                            onKeyDown={handleKeyPress}
                        />
                        {isSearchOpen && (
                            <button
                                type="button"
                                onClick={() => {
                                    setIsSearchOpen(false);
                                    onSearch("");
                                }}
                                className="absolute right-2 top-1/2 -translate-y-1/2 p-0.5 hover:bg-zinc-200 
                                    dark:hover:bg-zinc-700 rounded-full"
                            >
                                <X className="w-4 h-4" />
                            </button>
                        )}
                    </motion.div>
                    <button
                        type="button"
                        onClick={() => setIsSearchOpen(!isSearchOpen)}
                        className={`p-1.5 rounded-md transition-colors ${
                            isSearchOpen
                                ? "bg-zinc-100 dark:bg-zinc-800"
                                : "hover:bg-zinc-100 dark:hover:bg-zinc-800"
                        }`}
                    >
                        <Search className="w-4 h-4" />
                    </button>
                    <button
                        type="button"
                        onClick={onCartClick}
                        className="p-1.5 hover:bg-zinc-100 dark:hover:bg-zinc-800 rounded-md relative"
                    >
                        <ShoppingBag className="w-4 h-4" />
                        {cartItemCount > 0 && (
                            <motion.span
                                initial={{ scale: 0.5 }}
                                animate={{ scale: 1 }}
                                className="absolute -top-1 -right-1 bg-zinc-900 dark:bg-white 
                                    text-white dark:text-zinc-900 text-xs font-medium w-4 h-4 
                                    flex items-center justify-center rounded-full"
                            >
                                {cartItemCount}
                            </motion.span>
                        )}
                    </button>
                </div>
            </div>
        </div>
    );
}

import { motion } from "motion/react";
import { type Product } from "./data";

interface ProductGridProps {
    products: Product[];
    onProductSelect: (product: Product) => void;
}

export function ProductGrid({ products, onProductSelect }: ProductGridProps) {
    return (
        <div className="grid grid-cols-3 sm:grid-cols-4 lg:grid-cols-6 gap-2">
            {products.map((product) => (
                <motion.div
                    key={product.id}
                    layoutId={`product-${product.id}`}
                    onClick={() => onProductSelect(product)}
                    className="group cursor-pointer"
                    whileHover={{ y: -1 }}
                    transition={{ duration: 0.2 }}
                >
                    <div className="aspect-[4/5] bg-white dark:bg-zinc-900 rounded-md overflow-hidden">
                        <img
                            src={product.image}
                            alt={product.name}
                            className="w-full h-full object-cover object-center group-hover:scale-105 transition-transform duration-300"
                        />
                    </div>
                    <div className="mt-1.5 space-y-0.5">
                        <h3 className="text-xs font-medium truncate">
                            {product.name}
                        </h3>
                        <div className="flex justify-between items-center">
                            <p className="text-xs text-zinc-500 dark:text-zinc-400">
                                ${product.price}
                            </p>
                            <p className="text-[10px] text-zinc-400 dark:text-zinc-500">
                                {product.category}
                            </p>
                        </div>
                    </div>
                </motion.div>
            ))}
        </div>
    );
}

"use client";

import { motion } from "motion/react";
import { X, Plus, Minus } from "lucide-react";
import { useState } from "react";
import { type Product } from "./data";

interface ProductModalProps {
    product: Product;
    onClose: () => void;
    onAddToCart: (product: Product) => void;
}

export function ProductModal({
    product,
    onClose,
    onAddToCart,
}: ProductModalProps) {
    const [quantity, setQuantity] = useState(1);

    return (
        <>
            <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 0.5 }}
                exit={{ opacity: 0 }}
                className="fixed inset-0 bg-black"
                onClick={onClose}
            />
            <motion.div
                layoutId={`product-${product.id}`}
                className="fixed inset-x-4 bottom-0 md:inset-[25%] z-50 bg-white dark:bg-zinc-900 rounded-t-xl md:rounded-xl overflow-hidden max-h-[80vh] md:max-h-[500px]"
            >
                <div className="h-full md:flex">
                    <div className="relative md:w-2/5">
                        <img
                            src={product.image}
                            alt={product.name}
                            className="w-full h-[200px] md:h-full object-cover"
                        />
                        <button
                            onClick={onClose}
                            className="absolute top-2 right-2 p-1.5 bg-white/80 dark:bg-black/50 backdrop-blur-sm rounded-full"
                        >
                            <X className="w-4 h-4" />
                        </button>
                    </div>

                    <div className="p-3 md:w-3/5 flex flex-col">
                        <div className="flex-1">
                            <div className="flex justify-between items-start mb-2">
                                <div>
                                    <h2 className="text-sm font-medium">{product.name}</h2>
                                    <p className="text-xs text-zinc-500 dark:text-zinc-400">
                                        {product.category}
                                    </p>
                                </div>
                                <p className="text-sm font-medium">${product.price}</p>
                            </div>
                            <div className="space-y-2">
                                <p className="text-xs text-zinc-600 dark:text-zinc-300">
                                    {product.description}
                                </p>
                                <div className="text-xs space-y-1">
                                    <p className="text-zinc-500">SKU: {product.id}</p>
                                    <p className="text-zinc-500">Stock: Available</p>
                                </div>
                            </div>
                        </div>
                        <button
                            onClick={() => onAddToCart(product)}
                            className="w-full mt-3 py-2 bg-zinc-900 dark:bg-white text-white dark:text-zinc-900 text-xs font-medium rounded-md hover:bg-zinc-800 dark:hover:bg-zinc-100 transition-colors"
                        >
                            Add to Cart
                        </button>
                    </div>
                </div>
            </motion.div>
        </>
    );
}

import { motion } from "motion/react";
import { X } from "lucide-react";
import { type CartItem } from "./data";

interface CartDrawerProps {
    cart: CartItem[];
    onClose: () => void;
    onRemoveFromCart: (productId: string) => void;
}

export function CartDrawer({
    cart,
    onClose,
    onRemoveFromCart,
}: CartDrawerProps) {
    const total = cart.reduce(
        (sum, item) => sum + item.price * item.quantity,
        0
    );

    return (
        <>
            <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 0.5 }}
                exit={{ opacity: 0 }}
                className="fixed inset-0 bg-black"
                onClick={onClose}
            />
            <motion.div
                initial={{ x: "100%" }}
                animate={{ x: 0 }}
                exit={{ x: "100%" }}
                className="fixed right-0 top-0 h-full w-full sm:w-[400px] bg-white dark:bg-zinc-900 shadow-xl"
            >
                <div className="flex flex-col h-full">
                    <div className="flex items-center justify-between p-4 border-b border-zinc-200 dark:border-zinc-800">
                        <h2 className="text-lg font-medium">Shopping Cart</h2>
                        <button
                            onClick={onClose}
                            className="p-2 hover:bg-zinc-100 dark:hover:bg-zinc-800 rounded-full"
                        >
                            <X className="w-5 h-5" />
                        </button>
                    </div>

                    <div className="flex-1 overflow-y-auto p-4 space-y-4">
                        {cart.map((item) => (
                            <div
                                key={item.id}
                                className="flex gap-4 p-3 bg-zinc-50 dark:bg-zinc-800/50 rounded-lg"
                            >
                                <img
                                    src={item.image}
                                    alt={item.name}
                                    className="w-24 h-24 object-cover rounded-md"
                                />
                                <div className="flex-1 min-w-0">
                                    <div className="flex justify-between items-start">
                                        <h3 className="text-base font-medium truncate">
                                            {item.name}
                                        </h3>
                                        <button
                                            onClick={() =>
                                                onRemoveFromCart(item.id)
                                            }
                                            className="p-1.5 hover:bg-zinc-200 dark:hover:bg-zinc-700 rounded-full ml-2"
                                        >
                                            <X className="w-4 h-4" />
                                        </button>
                                    </div>
                                    <p className="text-sm text-zinc-500 dark:text-zinc-400 mt-1">
                                        Qty: {item.quantity}
                                    </p>
                                    <p className="text-base font-medium mt-1">
                                        ${item.price * item.quantity}
                                    </p>
                                </div>
                            </div>
                        ))}
                    </div>

                    <div className="p-4 border-t border-zinc-200 dark:border-zinc-800">
                        <div className="flex justify-between mb-4">
                            <span className="text-base">Total</span>
                            <span className="text-base font-medium">${total}</span>
                        </div>
                        <button className="w-full py-3 bg-zinc-900 dark:bg-white text-white dark:text-zinc-900 text-base font-medium rounded-lg hover:bg-zinc-800 dark:hover:bg-zinc-100 transition-colors">
                            Checkout
                        </button>
                    </div>
                </div>
            </motion.div>
        </>
    );
}

